function toggleSidebarArtistFilter(){$sidebarFiltrosBtn.click(function(a){a.preventDefault(),"Filtrar"===$sidebarFiltrosBtn.text()?($sidebarFiltrosBtn.text("Filtrar categorias").addClass("sidebar-categorias__btn--isabove"),$sidebar.addClass("sidebar--slide"),$body.addClass("sidebar-overlay")):($sidebarFiltrosBtn.text("Filtrar").removeClass("sidebar-categorias__btn--isabove"),$sidebar.removeClass("sidebar--slide"),$body.removeClass("sidebar-overlay"))})}